import { StyleSheet } from "react-native";


export const styles = StyleSheet.create({
    
    container: {
        width: '100%',
        backgroundColor: '#1F1E25',
        borderRadius: 5,
        flexDirection: "row",

    },

    name: {
        flex: 1,
        fontSize: 16,
        color: '#fff',
        marginLeft: 16

    },

    buttonText: {
        color: '#fff',
        fontSize: 24
      },
  
      button:{
        width: 56,
        heigh: 56,
        borderRadius: 5,
        backgroundColor: '#E23C44',
        alignItems: 'center',
        justifyContent: 'center'
      }
})
