import { StyleSheet } from "react-native"

export const styles = StyleSheet.create({
    container:{
      flex: 1,
      backgroundColor:'#131016',
      padding: 24
    },
  
    eventName: {
      color: '#fff',
      fontSize: 24,
      fontWeight: 'bold',        
      marginTop: 48
    },
  
    eventDate: {
      color: '#6b6b6b',
      fontSize: 16
    },

    input: {
      height: 56,
      backgroundColor: '#1F1E25',
      borderRadius: 5,
      color: '#fff',
      padding: 16,
      fontSize: 16,
      
    },

    buttonText: {
      color: '#fff',
      fontSize: 24
    },

    button:{
      width: 56,
      heigh: 56,
      borderRadius: 5,
      backgroundColor: '#31CF67',
      alignItems: 'center',
      justifyContent: 'center'
    }

  })
